INSERT INTO recipes VALUES('28fde7a2-c4fc-8137-ac68-e9949688f7f8','Sourdough Christmas Star','â­',NULL,NULL,NULL,NULL,0,1760729616541,1760729616541,0,replace('\nCinnamon dough:\n\n\n2 11/4 kamut or 2 1/2 ap flour\n\n\n2 tbsp sugar\n\n\n1 tsp baking powder\n\n\n1/2 tsp baking soda\n\n\n1 tsp salt\n\n\n1/2 cup butter\n\n\n1 cup milk\n\n\n1/2 cup sourdough discard\n\n\nFilling:\n\n\n1/2 cup butter\n\n\n1/2 cup sugar\n\n\n1/2 cup brown sugar\n\n\n2 tsp cinnamon\n\n- stir together flour, sugar, baking powder, baking soda and salt. Grate butter on top and mix together to form a crumbly mix.\n- add milk and discard, mix to for a cohesive dough. Knead for a few minutes until soft. Place in bowl and let rise for around 4 hours on the counter and then an additional over night ferment in the fridge (optional!).\n- melt filling ingredients together in a pot.\n- cut dough into four sections. Roll out each section, adding filling to the first three layers. Place the final layer on top.\n- cut into 16 sections as seen in video. Twist away from one another to form the star limbs.\n- beat an egg and brush on top. Sprinkle with coarse sugar.\n','\n',char(10)),'["2 1/4 cups kamut flour or 2 1/2 cups all-purpose flour","2 tbsp sugar","1 tsp baking powder","1/2 tsp baking soda","1 tsp salt","1/2 cup butter","1 cup milk","1/2 cup sourdough discard","1/2 cup butter (for filling)","1/2 cup sugar (for filling)","1/2 cup brown sugar","2 tsp cinnamon","1 egg","Coarse sugar for topping"]',NULL,NULL,NULL,'team_default','public');
