INSERT INTO recipes VALUES('28fde7a2-c4fc-81de-a889-e0e6cee0de43','BBQ Ribs','ğŸ®',NULL,NULL,NULL,NULL,0,1760729616541,1760729616541,0,replace('\nIngredients:\n\n- 1 rack of baby back ribs\n- 1 red onion\n- 5 garlics\n- 1 tsp fennel\n- 2.5 cups of ketchup\n- 1/2 cup of soy\n- 1 cup of brown sugar\n- 1 tsp of chili flakes\n\nDirections:\n\n\n1. Preheat over to 265 degrees F\n\n\n2. Cook the diced onions in oil for 3-4 minutes.\n\n\n3. Add in Garlic, Fennel, and Chili flakes and cook for another 2 minutes\n\n\n4. Add in brown sugar and cook down till everything is emulsified\n\n\n5. Add in Ketchup and Soy sauce and cook for another 2-3 minutes\n\n\n6. Put the sauce all over the ribs. Make sure to get it on the bottom as well.\n\n\n7. Cover the ribs with foil and make sure itâ€™s tight. No air should get out.\n\n\n8. Cook for about 1.5-2 hours depending on the thickness.\n\n\n9. Take out the oven, remove the top cover and re-coat the ribs with the sauce.\n\n\n10. Increase heat to 325 degrees F and place the ribs in for another 30 minutes\n\n\n11. Take it out, let it sit and cool down for a few minutes an enjoy!\n\n','\n',char(10)),'["1 rack of baby back ribs","1 red onion","5 garlics","1 tsp fennel","2.5 cups of ketchup","1/2 cup of soy","1 cup of brown sugar","1 tsp of chili flakes"]',NULL,NULL,NULL,'team_default','public');
