INSERT INTO recipes VALUES('28fde7a2-c4fc-8194-b588-e4977cbea5cf','Thai Peanut Chicken Ramen','ğŸ²',NULL,NULL,NULL,NULL,0,1760729616542,1760729616542,0,replace('\n### Ingredients\n\n- 4Â cupsÂ [low sodium chicken broth](http://l.thrv.me/HBH4263-pacific-foods-organic-low-sodium)\n- 1Â can (14 ounce)Â [coconut milk](http://l.thrv.me/HBH5625-thrive-market-coconut-milk-regul)\n- 1/4Â cupÂ [low sodium soy sauce](http://l.thrv.me/HBH4794-san-j-international-tamari-glute)\n- 2Â tablespoonsÂ [fish sauce](http://l.thrv.me/HBH4628-red-boat-fish-sauce-fish-sauce)\n- 2Â tablespoonsÂ [honey](http://l.thrv.me/HBH5905-thrive-market-organic-raw-unstra)\n- 1/3Â cupÂ [creamy peanut butter](http://l.thrv.me/HBH4796-santa-cruz-organic-creamy-peanut)\n- 1/4Â cupÂ [Thai red curry paste](http://thrv.me/WQkVzj)\n- 3/4Â poundÂ boneless, skinless chicken breasts\n- 8Â ouncesÂ cremini mushrooms, sliced\n- 2Â red bell peppers, chopped\n- 1Â inchÂ fresh ginger, grated\n- 1Â cloveÂ garlic, minced or grated\n- 2-4Â squaresÂ [ramen noodles](http://l.thrv.me/HBH1962-lotus-foods-organic-jade-pearl-r)\n- juice of 1 lime\n- 3Â cupsÂ fresh baby spinach\n- 1/3Â cupÂ fresh basil or cilantro, roughly chopped, plus more for serving\n- chopped peanuts and toasted sesame oil, for serving\n','\n',char(10)),'["4 cups low sodium chicken broth","1 can (14 ounce) coconut milk","1/4 cup low sodium soy sauce","2 tablespoons fish sauce","2 tablespoons honey","1/3 cup creamy peanut butter","1/4 cup Thai red curry paste","3/4 pound boneless, skinless chicken breasts","8 ounces cremini mushrooms, sliced","2 red bell peppers, chopped","1 inch fresh ginger, grated","1 clove garlic, minced or grated","2-4 squares ramen noodles","juice of 1 lime","3 cups fresh baby spinach","1/3 cup fresh basil or cilantro, roughly chopped, plus more for serving","chopped peanuts and toasted sesame oil, for serving"]',NULL,NULL,NULL,'team_default','public');
INSERT INTO recipes VALUES('28fde7a2-c4fc-81af-b854-cf9c531a00ce','Chicken kofta (but with beef)','ğŸ”',NULL,NULL,NULL,NULL,0,1760729616542,1760729616542,0,replace('\nIngredients:\n\n\nFor the Chicken Kofta:\n\n- 1 lb. Ground Chicken (96% lean, not breast only)\n- 1/2 Cup Yellow Onion, finely chopped\n- 1/4 Cup Parsley, finely chopped\n- 2 Tbsp Mint, finely chopped\n- 3 Cloves Garlic, minced\n- 1 Egg\n- 1 Tbsp Coriander\n- 1 1/2 tsp Cumin\n- 1/2 tsp Cinnamon\n- 1/4 tsp Cayenne Pepper\n- 1 Pinch Cloves\n- 1/4 tsp Black Pepper\n- 1 tsp Kosher Salt\n- 1 Tbsp Avocado or Olive Oil\n\nFor the Tzatziki: \n\n- 1/2 a Large Cucumber, grated\n- 2 Cups 2% Greek Yogurt\n- 1 Clove Garlic, grated or minced\n- 1 Lemon, juice\n- 1 Tbsp Olive Oil\n- 1/4 tsp Kosher Salt (or to taste)\n\nFor the Lemon Dill Rice:\n\n- 2 Cups Chicken Stock\n- 1/2 tsp Salt\n- 1 Clove Garlic, minced\n- 1 Cup Long-Grain Rice\n- 1 Medium Lemon, zest (~1 Tbsp)\n- 2 Tbsp Fresh Dill, chopped\n- 2 Tbsp Butter\n\nSliced Veggies (for serving):\n\n- 1/2 a Cucumber, sliced\n- 1/4 of a Yellow Onion, sliced\n- 1 Tomato, sliced\n\n1ï¸âƒ£ Mix together ingredients for the tzatziki and set aside in the fridge\n\n\n2ï¸âƒ£ To make the lemon dill rice, add the chicken stock, salt, and garlic to a small pot and bring to a boil; stir in the rice and then turn the heat down to low and cover and simmer for 20 mins (or until all liquid is absorbed); turn off the heat and stir in the lemon zest, dill, and butter\n\n\n3ï¸âƒ£ For the chicken kofta, combine the ground chicken, onion, parsley, mint, garlic, egg, coriander, cumin, cinnamon, cayenne, cloves, salt, and pepper in a large bowl\n\n\n4ï¸âƒ£ Preheat a large nonstick skillet over medium heat and add 1 Tbsp oil; place dollops of the chicken mixture (~1 heaping Tbsp each) onto the pan and cook for 2-3 minutes until browned; flip over and flatten slightly with the spatula, and cook for another 2-3 minutes until internal temp reaches 165F; repeat until all chicken is cooked\n\n\n5ï¸âƒ£ Serve chicken kofta with lemon dill rice, tzatziki, and sliced veggies and enjoy!\n\n','\n',char(10)),'["1 lb. Ground Chicken (96% lean, not breast only)","1/2 Cup Yellow Onion, finely chopped","1/4 Cup Parsley, finely chopped","2 Tbsp Mint, finely chopped","3 Cloves Garlic, minced","1 Egg","1 Tbsp Coriander","1 1/2 tsp Cumin","1/2 tsp Cinnamon","1/4 tsp Cayenne Pepper","1 Pinch Cloves","1/4 tsp Black Pepper","1 tsp Kosher Salt","1 Tbsp Avocado or Olive Oil","1/2 a Large Cucumber, grated","2 Cups 2% Greek Yogurt","1 Clove Garlic, grated or minced","1 Lemon, juice","1 Tbsp Olive Oil","1/4 tsp Kosher Salt (or to taste)","2 Cups Chicken Stock","1/2 tsp Salt","1 Clove Garlic, minced","1 Cup Long-Grain Rice","1 Medium Lemon, zest (~1 Tbsp)","2 Tbsp Fresh Dill, chopped","2 Tbsp Butter","1/2 a Cucumber, sliced","1/4 of a Yellow Onion, sliced","1 Tomato, sliced"]',NULL,NULL,NULL,'team_default','public');
