INSERT INTO recipes VALUES('28fde7a2-c4fc-81d9-a170-c7f9865005e7','Savory Sweet Meatballs','ğŸ®',NULL,NULL,NULL,NULL,0,1760729616542,1760729616542,0,replace('\nIngredients: \n\n\n**Beef option**\n2 lbs ground beef\n1/4 cup cassava flour (omit)\n1 cup bone broth (omit) \n1.5 TBS salt\n1 tsp onion powder\n6 garlic cloves, minced (or 1 tsp garlic\npowder)\n1 tsp turmeric\n1/2 tsp powdered ginger\n1/2 tsp cayenne\n\n\n**Sauce**\n\n\n1 cup balsamic vinegar\n4.5 TBS honey\n2 tsp salt\n1 tsp garlic powder\n\n\nInstructions: \n\n\n(meatballs)\n\n\n1.Preheat oven to 350 degrees.\n2.Combine ingredients in a large bowl and\nmix until everything is incorporated.\n3.Wet hands, shape into balls (around 24),\nand place on a parchment paper-lined\nbaking sheet.\n4.Bake for 22-25 minutes (check the center\nfor pink).\n5.Put finished meatballs into a large bowl\nand top with the sauce, stirring gently to\ncombine.\nServes 8.\n\n\n(sauce)\n\n\n1.Add all ingredients to a saucepan\nand bring to a simmer.\n2.Reduce heat to low, cover, and let\nthicken. Stir occasionally until\nreduced to about half the original\namount (may take around 15-20\nmins, depending on how thick youâ€™d\nlike the sauce). The sauce will\nthicken a bit more as it cools.\n3.Pour over meatballs and enjoy\n\n','\n',char(10)),'["2 lbs ground beef","1/4 cup cassava flour (omit)","1 cup bone broth (omit)","1.5 TBS salt","1 tsp onion powder","6 garlic cloves, minced (or 1 tsp garlic powder)","1 tsp turmeric","1/2 tsp powdered ginger","1/2 tsp cayenne","1 cup balsamic vinegar","4.5 TBS honey","2 tsp salt","1 tsp garlic powder"]',NULL,NULL,NULL,'team_default','public');
