INSERT INTO recipes VALUES('28fde7a2-c4fc-8192-a955-cf0d2e95db92','Sweet Potato Breakfast Sausage Hash (ChatGPT)','ğŸ–',NULL,NULL,NULL,NULL,0,1760729616542,1760729616542,0,replace('\n### ğŸ³ Sweet Potato & Breakfast Sausage Hash\n\n\nIngredients (serves 4â€“6):\n\n- 1 lb breakfast ground sausage (pork or turkey works)\n- 2â€“3 medium sweet potatoes, peeled and diced into Â½-inch cubes\n- 1 medium onion, diced (yellow or red)\n- 1 bell pepper, diced (optional, for color + flavor)\n- 2 cloves garlic, minced\n- 2â€“3 Tbsp butter, bacon fat, or tallow (ancestral-friendly cooking fat)\n- 1 tsp smoked paprika (or regular paprika)\n- Â½ tsp dried thyme or rosemary\n- Sea salt + black pepper to taste\n- Fresh parsley or green onion for garnish\n\nOptional add-ons:\n\n- A handful of spinach or kale (stirred in at the end)\n- A fried or poached egg on top of each serving\n\n---\n\n\nInstructions:\n\n1. Cook the sausage: In a large cast iron skillet, cook the sausage over medium heat until browned and crumbly. Remove and set aside, leaving some fat in the pan.\n2. Cook the sweet potatoes: Add 1â€“2 Tbsp fat if needed, then toss in the sweet potatoes. Cook over medium heat, stirring occasionally, until they begin to soften and get golden edges (about 12â€“15 minutes).\n3. Add aromatics: Stir in onion, bell pepper, and garlic. Cook until the onion is translucent and peppers are soft (5â€“7 minutes).\n4. Season + combine: Sprinkle in paprika, thyme/rosemary, salt, and pepper. Return sausage to the skillet, mixing everything together. Cook another 3â€“5 minutes until flavors meld.\n5. Finish + serve: Taste and adjust seasoning. Garnish with parsley or green onion. Optionally, top each portion with a fried or poached egg.\n','\n',char(10)),'["1 lb breakfast ground sausage (pork or turkey works)","2â€“3 medium sweet potatoes, peeled and diced into Â½-inch cubes","1 medium onion, diced (yellow or red)","1 bell pepper, diced (optional, for color + flavor)","2 cloves garlic, minced","2â€“3 Tbsp butter, bacon fat, or tallow (ancestral-friendly cooking fat)","1 tsp smoked paprika (or regular paprika)","Â½ tsp dried thyme or rosemary","Sea salt + black pepper to taste","Fresh parsley or green onion for garnish","A handful of spinach or kale (stirred in at the end)","A fried or poached egg on top of each serving"]',NULL,NULL,NULL,'team_default','public');
